import classes from "./SolutionDesc.module.css";
import { Container } from "@mui/material";

const SolutionDesc = (props) => {
  const { heading, subHeading, content, image } = props;

  return (
    <Grid container>
      <Grid item xs={1} sm={3} sx={{ pt: 8, overflow: "hidden" }}>
        <h1 className={classes.bigHeader}>
          <span style={{ color: "#a1a1a6" }}>{heading}.</span>
          <br /> {subHeading}.
        </h1>
        <h2 style={{ color: "#a1a1a6" }}>{content}</h2>
      </Grid>
      <Grid item xs={1} sm={3} sx={{ pt: 8, overflow: "hidden" }}>
        <h1 className={classes.bigHeader}>
          <span style={{ color: "#a1a1a6" }}>{heading}.</span>
          <br /> {subHeading}.
        </h1>
        <h2 style={{ color: "#a1a1a6" }}>{content}</h2>
      </Grid>
    </Grid>
  );
};

<div
  style={{
    overflow: "hidden",
    textAlign: "center",
    backgroundImage: `url(${image})`,
    backgroundRepeat: "round",
    backgroundSize: "contain",
  }}
>
  <Grid container>
    <Grid item xs={1} sm={3}></Grid>
    <Grid item xs={12} sm={6} sx={{ textAlign: "center", pb: 6, px: 2 }}>
      <h1>{name}</h1>
      <p>{description}</p>
    </Grid>
    <Grid item xs={1} sm={3}></Grid>
  </Grid>
</div>;
export default SolutionDesc;

