import classes from "./SolutionDesc.module.css";
import { Container, Grid } from "@mui/material";

const SolutionDesc = (props) => {
  const { heading, subHeading, content, image } = props;

  return (
    <Grid container >
      <Grid item xs={12} sm={7}>
        <Container sx={{ pt: 8,px: 6, overflow: "hidden" }}>
        <h1 className={classes.bigHeader}>
          <span style={{ color: "#a1a1a6" }}>{heading}.</span>
          <br /> {subHeading}.
        </h1>
        <h2 style={{ color: "#a1a1a6" }}>{content}</h2>
        </Container>
      </Grid>
    </Grid>
  );
};

export default SolutionDesc;

<Grid container>
        <Grid item xs={12} sm={7}>
          <Container sx={{ pt: 8,px: 6, overflow: "hidden" }}>
            <h3>{heading}</h3>
          </Container>
          <Container sx={{ py: 6, mx: 2 }}>
            {highlights && (
              <h1 className={classes.bigHeader}>
                <span style={{ color: '#4693ad' }}>{highlights[0]}</span> <br /> {highlights[1]}. <br /> {highlights[2]}
              </h1>
            )}
            <Container sx={{}}>
            <img alt="card-3" className={classes.image} src={image}/>
          </Container>

          </Container>
        </Grid>
        <Grid item xs={12} sm={5} sx={{ alignItems: 'center', justifyContent: 'center', display: 'flex' }}>
          <Container sx={{ py: 4 }}>
            <h3>{content}</h3>
          </Container>
        </Grid>
      </Grid>
